mapper.select:
		select count(lg_num)
			from league
			where lg_state like #{state}
			
  				and lg_re_num= #{cri.type}

		select * from league
			where 
			(lg_name like concat('%',#{cri.search} ,'%'))
			
  				and lg_re_num= #{cri.type}  				
  			
  			order by lg_num desc
			limit #{cri.pageStart}, #{cri.perPageNum}
