mapper.select:
		select * from league-attribute
			
		
		where (la_name like concat('%',#{cri.search},'%')
  			or la_match_type like concat('%',#{cri.search},'%')
  		)
  		
  			and la_num = #{cri.type}
  		
  		order by la_num desc
  		limit #{cri.pageStart}, #{cri.perPageNum}

		select count(*) from league-attribute
  		where (la_name like concat('%',#{cri.search},'%')
  			or la_match_type like concat('%',#{cri.search},'%')
  		)
  		
  			and la_num = #{cri.type}
